class Solution(object):
    def increasingTriplet(self, nums):
        number_length = len(nums)
        if number_length < 3:
            return False
        a, b = 9999, 9999
        for i in range(len(nums)):
            if nums[i] <= a:
                a = nums[i]
            elif nums[i] <= b:
                b = nums[i]
            else:
                return True
        return False


class Solution2(object):
    def increasingTriplet(self, nums):
        if len(nums) == 0:
            return False
        self.LIS = [nums[0]]
        for i in range(1, len(nums)):
            num = nums[i]
            if num > self.LIS[-1]:
                self.LIS.append(num)
            else:
                index = self.binary_search(num)
                self.LIS[index] = num
            if len(self.LIS) > 2:
                return True

        return False

    def binary_search(self, num):
        start = 0
        end = len(self.LIS) - 1
        if self.LIS[0] >= num:
            return 0
        while end - start > 1:
            middle = (start + end) / 2
            if self.LIS[middle] > num:
                end = middle
            elif self.LIS[middle] < num:
                start = middle
            else:
                return middle
        return end


